package com.itssvkv.chatapp.data.local.repositoryimport com.google.android.gms.tasks.Taskimport com.google.firebase.Timestampimport com.google.firebase.firestore.CollectionReferenceimport com.google.firebase.firestore.DocumentReferenceimport com.google.firebase.firestore.Queryimport com.google.firebase.firestore.QuerySnapshotinterface FirebaseRepository {     fun currentUserId(): String?    suspend fun currentUserDetails(): DocumentReference    suspend fun getAllUsersCollectionRef(searchText: String): Task<QuerySnapshot>    suspend fun getChatRoomReference(chatRoomId: String): DocumentReference    suspend fun getChatRoomId(firstUserId: String, secondUserId: String): String    suspend fun getChatRoomMessageReference(chatRoomId: String): CollectionReference    suspend fun getAllMessageOfTwoUsers(chatRoomId: String):Query    suspend fun getAllChatRooms():Query     fun getOtherUserFromChatRoom(userIds:List<String>) : DocumentReference     fun changeTimestampToString(timestamp:Timestamp?) : String     suspend fun createPostOnFirebase():DocumentReference}