package com.itssvkv.chatapp.data.local.repositoryimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.firestore.DocumentReferenceimport com.google.firebase.firestore.FirebaseFirestoreimport com.itssvkv.chatapp.models.UserDataInfoimport javax.inject.Injectclass FirebaseRepositoryImpl@Inject constructor(    private val db: FirebaseFirestore,    private val auth: FirebaseAuth) : FirebaseRepository {    override suspend fun currentUserId() =        auth.uid    override suspend fun currentUserDetails(): DocumentReference {        return db.collection("friends").document(currentUserId()!!)    }}