package com.itssvkv.chatapp.data.local.repositoryimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.firestore.FirebaseFirestoreimport javax.inject.Injectclass FirebaseRepositoryImpl@Inject constructor(    private val db: FirebaseFirestore,    private val auth: FirebaseAuth) : FirebaseRepository {    override suspend fun currentUserId() =        auth.uid    override suspend fun currentUserDetails() =        db.collection("friends").document(currentUserId()!!)}