package com.itssvkv.chatapp.data.local.repositoryimport android.content.Contextimport com.itssvkv.chatapp.utils.sharedpref.SharedPrefimport javax.inject.Injectclass SharedPrefRepositoryImpl @Inject constructor() : SharedPrefRepository {    override suspend fun saveToPref(context: Context, key: String, value: Any) =        SharedPref.saveToPref(context = context, key = key, value = value)    override suspend fun getFromPref(context: Context, key: String, defValue: Any) =        SharedPref.getFromPref(context = context, key = key, defValue = defValue)}