package com.itssvkv.chatapp.diimport android.os.Bundleimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.firestore.FirebaseFirestoreimport com.google.firebase.storage.FirebaseStorageimport com.google.firebase.storage.StorageReferenceimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object BaseModule {    @Provides    @Singleton    fun providesFireStore(): FirebaseFirestore {        return FirebaseFirestore.getInstance()    }    @Provides    @Singleton    fun providesFireAuth(): FirebaseAuth {        return FirebaseAuth.getInstance()    }    @Provides    @Singleton    fun providesFireStorage(): StorageReference {        return FirebaseStorage.getInstance().reference    }    @Provides    @Singleton    fun providesBundle(): Bundle =        Bundle()}