package com.itssvkv.chatapp.diimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryImplimport com.itssvkv.chatapp.data.local.repository.SharedPrefRepositoryimport com.itssvkv.chatapp.data.local.repository.SharedPrefRepositoryImplimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object RepositoryModule {    @Provides    @Singleton    fun providesSharedPrefRepository(sharedPrefRepositoryImp: SharedPrefRepositoryImpl): SharedPrefRepository {        return sharedPrefRepositoryImp    }    @Provides    @Singleton    fun providesFirebaseRepository(firebaseRepositoryImpl: FirebaseRepositoryImpl): FirebaseRepository {        return firebaseRepositoryImpl    }}