package com.itssvkv.chatapp.ui.home.adaptersimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.request.RequestOptionsimport com.itssvkv.chatapp.databinding.PostsRecyclerBindingimport com.itssvkv.chatapp.models.Postsclass PostsAdapter :    androidx.recyclerview.widget.ListAdapter<Posts, PostsAdapter.PostsViewHolder>(Comparator) {    inner class PostsViewHolder(val binding: PostsRecyclerBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(posts: Posts) {            if (posts.postPhoto != "null") {                binding.postsLayout.visibility = View.VISIBLE                binding.postsLayoutWithOutPhoto.visibility = View.GONE                binding.postsLayoutWithOutCaption.visibility = View.GONE                Glide.with(binding.currentUserIv.context)                    .load(posts.userWhoCreateThePost?.profilePhoto)                    .apply(                        RequestOptions()                            .dontTransform()                    )                    .into(binding.currentUserIv)                Glide.with(binding.postPhoto.context)                    .load(posts.postPhoto)                    .apply(                        RequestOptions()                            .dontTransform()                    )                    .into(binding.postPhoto)                binding.currentUserName.text = posts.userWhoCreateThePost?.name                binding.postCaption.text = posts.caption            } else {                binding.postsLayoutWithOutPhoto.visibility = View.VISIBLE                binding.postsLayout.visibility = View.GONE                binding.postsLayoutWithOutCaption.visibility = View.GONE                Glide.with(binding.currentUserIvWithOutPhoto.context)                    .load(posts.userWhoCreateThePost?.profilePhoto)                    .apply(                        RequestOptions()                            .dontTransform()                    )                    .into(binding.currentUserIvWithOutPhoto)                binding.currentUserNameWithOutPhoto.text = posts.userWhoCreateThePost?.name                binding.postCaptionWithOutPhoto.text = posts.caption            }        }    }    private object Comparator : DiffUtil.ItemCallback<Posts>() {        override fun areItemsTheSame(oldItem: Posts, newItem: Posts): Boolean {            return oldItem.postId == newItem.postId        }        override fun areContentsTheSame(oldItem: Posts, newItem: Posts): Boolean {            return oldItem == newItem        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): PostsViewHolder {        val binding =            PostsRecyclerBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return PostsViewHolder(binding)    }    override fun onBindViewHolder(holder: PostsViewHolder, position: Int) {        val onePost = getItem(position)        onePost?.let { posts ->            holder.bind(posts = posts)        }    }}