package com.itssvkv.chatapp.ui.home.chatsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.google.firebase.firestore.QuerySnapshotimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport javax.inject.Inject@HiltViewModelclass ChatsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository) : ViewModel() {    private var _query = MutableLiveData<QuerySnapshot>()    val query: LiveData<QuerySnapshot>        get() = _query    suspend fun sendSearchQuery(searchText: String) =        firebaseRepository.getAllUsersCollectionRef(searchText)    suspend fun getAllChatRooms() =        firebaseRepository.getAllChatRooms().addSnapshotListener { value, _ ->            _query.postValue(value)        }}