package com.itssvkv.chatapp.ui.home.chatsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.google.firebase.firestore.QuerySnapshotimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass ChatsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository) : ViewModel() {    private var _query = MutableLiveData<QuerySnapshot>()    val query: LiveData<QuerySnapshot>        get() = _query    init {        getAllChatRooms()    }    suspend fun sendSearchQuery(searchText: String) =        firebaseRepository.getAllUsersCollectionRef(searchText)    private fun getAllChatRooms() =        viewModelScope.launch {            firebaseRepository.getAllChatRooms().addSnapshotListener { value, _ ->                _query.postValue(value)            }        }}