package com.itssvkv.chatapp.ui.home.chatsimport androidx.lifecycle.ViewModelimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport javax.inject.Inject@HiltViewModelclass ChatsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository) : ViewModel() {    suspend fun sendSearchQuery(searchText: String) =        firebaseRepository.getAllUsersCollectionRef(searchText)}