package com.itssvkv.chatapp.ui.home.currentUserProfileimport android.content.Contextimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.google.gson.Gsonimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport com.itssvkv.chatapp.data.local.repository.SharedPrefRepositoryimport com.itssvkv.chatapp.models.UserDataInfoimport com.itssvkv.chatapp.utils.sharedpref.SharedPrefCommon.CURRENT_USER_INFOimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass MyProfileViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository,    private val sharedPrefRepository: SharedPrefRepository) : ViewModel() {    private val _currentUserInfoLiveData = MutableLiveData<UserDataInfo>()    val currentUserInfoLiveData: LiveData<UserDataInfo>        get() = _currentUserInfoLiveData    private val _currentUserDataInfo = MutableLiveData<UserDataInfo>()    val currentUserDataInfo: LiveData<UserDataInfo>        get() = _currentUserDataInfo    suspend fun getCurrentUserInfo() {        firebaseRepository.currentUserDetails().get().addOnSuccessListener {            _currentUserInfoLiveData.postValue(it.toObject(UserDataInfo::class.java))        }    }    fun getCurrentUserFromSharedPref(context: Context) {        viewModelScope.launch {            val currentUserInfo = sharedPrefRepository.getFromPref(                context = context,                key = CURRENT_USER_INFO,                defValue = ""            ) as String            _currentUserDataInfo.postValue(                Gson().fromJson(                    currentUserInfo,                    UserDataInfo::class.java                )            )        }    }}