package com.itssvkv.chatapp.ui.home.postsimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport com.itssvkv.chatapp.models.Postsimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass PostsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository) : ViewModel() {    private val _allPostsLiveData = MutableLiveData<MutableList<Posts>>()    val allPostsLiveData: LiveData<MutableList<Posts>>        get() = _allPostsLiveData    init {        getAllPostFromFirebase()    }    private fun getAllPostFromFirebase() {        viewModelScope.launch {            firebaseRepository.getAllPostFromFirebase().addSnapshotListener { value, _ ->                _allPostsLiveData.postValue(value?.toObjects(Posts::class.java))            }        }    }}