package com.itssvkv.chatapp.ui.home.postsimport android.content.Contextimport android.util.Logimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.google.gson.Gsonimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport com.itssvkv.chatapp.data.local.repository.SharedPrefRepositoryimport com.itssvkv.chatapp.models.Postsimport com.itssvkv.chatapp.models.UserDataInfoimport com.itssvkv.chatapp.utils.Common.TAGimport com.itssvkv.chatapp.utils.sharedpref.SharedPrefCommon.CURRENT_USER_INFOimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass PostsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository,    private val sharedPrefRepository: SharedPrefRepository) : ViewModel() {    private val _allPostsLiveData = MutableLiveData<MutableList<Posts>>()    val allPostsLiveData: LiveData<MutableList<Posts>>        get() = _allPostsLiveData    init {        getAllPostFromFirebase()    }    private fun getAllPostFromFirebase() {        viewModelScope.launch {            firebaseRepository.getAllPostFromFirebase().addSnapshotListener { value, _ ->                _allPostsLiveData.postValue(value?.toObjects(Posts::class.java))            }        }    }}