package com.itssvkv.chatapp.ui.home.settingimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.itssvkv.chatapp.data.local.repository.FirebaseRepositoryimport com.itssvkv.chatapp.models.UserDataInfoimport dagger.hilt.android.lifecycle.HiltViewModelimport javax.inject.Inject@HiltViewModelclass SettingsViewModel @Inject constructor(    private val firebaseRepository: FirebaseRepository) : ViewModel() {    private val _currentUserInfoLiveData = MutableLiveData<UserDataInfo>()    val currentUserInfoLiveData: LiveData<UserDataInfo>        get() = _currentUserInfoLiveData    suspend fun getCurrentUserInfo() {        firebaseRepository.currentUserDetails().get().addOnSuccessListener {            _currentUserInfoLiveData.postValue(it.toObject(UserDataInfo::class.java))        }    }}