package com.itssvkv.chatapp.ui.onboardingimport android.content.Contextimport androidx.lifecycle.ViewModelimport com.itssvkv.chatapp.data.local.repository.SharedPrefRepositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport javax.inject.Inject@HiltViewModelclass OnBoardingViewModel @Inject constructor(    private val repo: SharedPrefRepository) : ViewModel() {    suspend fun saveToPref(context: Context, key: String, value: Any) =        repo.saveToPref(context, key, value)}