package com.itssvkv.chatapp.ui.onboarding.pagerimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterclass PagerAdapter(fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int = 3    override fun createFragment(position: Int): Fragment {        when (position) {            0 -> return FirstFragment()            1 ->                return SecondFragment()            2 -> return ThirdFragment()        }        return FirstFragment()    }}